package com.home.preparations;

public class MultiplyFloating {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		float a1 = 5.2f, b1=2.3f;
		
		float product = a1*b1;
		
		
		
		System.out.println("The product of two floating numbers are " + (a1*b1));
		System.out.println("The product of two floating numbers are " + product);
		
		
	}

}
